import React from "react";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/Table";
import TableHeader from "@material-ui/core/Table";
import TableHeaderColumn from "@material-ui/core/Table";
import TableRow from "@material-ui/core/Table";
import "./App.css";
import socketIOClient from "socket.io-client";
import Axios from "axios";

class App extends React.Component {
  constructor() {
    super();
    this.socket = socketIOClient("http://localhost:8080");
    this.socket.emit("new");
    this.state = {
      items: []
    };
  }
  /*  async componentDidMount() {
    const data = await fetch("http://localhost:8080/items", {
      methog: "GET",
      headers: {
        "Content-Type": "application/json",
        Accept: "applicaiton/json"
      }
    })
      .then(function(res) {
        return res.json();
      })
      .then(function(res) {
        return res;
      })
      .catch(function(err) {
        throw err;
      });
    console.log("bonjour");
    console.log(data);
    this.setState({ items: data });
  }*/
  async getItem() {
    const data = await Axios.get("http://localhost:8080/items");
    console.log(data);
    this.setState({ items: data });
  }

  render() {
    if (this.state.items === undefined) {
      this.getItem();
      console.log("salut");
      return (
        <div>
          <header className="App-header">Loading</header>
        </div>
      );
    }
    return (
      <div className="App">
        <Table>
          <TableRow>
            {this.state.items.map((items, index) => {
              return (
                <TableHeaderColumn key={index}>{items.name}</TableHeaderColumn>
              );
            })}
          </TableRow>
        </Table>
      </div>
    );
  }
}

export default App;
