import React from "react";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import MaterialTable from "material-table";
import "./App.css";
import socketIOClient from "socket.io-client";
import Axios from "axios";

class App extends React.Component {
  constructor() {
    super();
    this.socket = socketIOClient("http://localhost:8080");
    this.socket.emit("new");
    this.state = {
      columns: [
        { title: "Name", field: "name" },
        { title: "Type", field: "type" },
        { title: "Price", field: "price", type: "numeric" },
        { title: "rating", field: "rating", type: "numeric" },
        { title: "warranty years", field: "warranty_years", type: "numeric" }
      ],
      items: []
    };
  }
  handleClick() {
    console.log("clicked");
  }

  async getItem() {
    const data = await Axios.get("http://localhost:8080/items");
    //    console.log(data);
    const data_string = JSON.stringify(data.data);
    console.log(data_string);
    const data_array = JSON.parse(data_string);
    console.log(data_array);
    this.setState({ items: data_array });
    console.log(this.state.items.length);
  }
  render() {
    if (this.state.items.length < 1) {
      console.log(this.state.items.length);
      this.getItem();
      console.log("salut");
      return (
        <div>
          <header className="App-header">Loading</header>
        </div>
      );
    }
    console.log(this.state.items);
    return (
      /*
      <TableContainer component={Paper}>
        <Table className="" aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Items</TableCell>
              <TableCell align="right">type</TableCell>
              <TableCell align="right">price</TableCell>
              <TableCell align="right">rating</TableCell>
              <TableCell align="right">warranty years</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {this.state.items.map(item => (
              <TableRow key={item.name}>
                <TableCell component="th" scope="row">
                  {item.name}
                </TableCell>
                <TableCell align="right">{item.type}</TableCell>
                <TableCell align="right">{item.price}€</TableCell>
                <TableCell align="right">{item.rating}</TableCell>
                <TableCell align="right">{item.warranty_years}</TableCell>
                <TableCell>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={this.handleClick}
                  >
                    modify
                  </Button>
                  <Button variant="contained" color="secondary">
                    delete
                  </Button>
                </TableCell>
              </TableRow>
            ))}
            <TableRow>
              <TableCell>
                <TextField id="standard-basic" label="Name" align="right" />
              </TableCell>
              <TableCell>
                <TextField id="standard-basic" label="Type" align="right" />
              </TableCell>
              <TableCell>
                <TextField id="standard-basic" label="Price" align="right" />
              </TableCell>
              <TableCell>
                <TextField id="standard-basic" label="Rating" align="right" />
              </TableCell>
              <TableCell>
                <TextField id="standard-basic" label="Warranty" align="right" />
              </TableCell>
              <TableCell>
                <Button variant="contained">add</Button>
              </TableCell>
            </TableRow>
          </TableBody>
        </Table>
      </TableContainer>*/
      <MaterialTable
        title="Item list"
        columns={this.state.columns}
        data={this.state.items}
        editable={{
          onRowAdd: newData =>
            new Promise(resolve => {
              setTimeout(() => {
                resolve();
                this.setState(prevState => {
                  const data = [...prevState.data];
                  data.push(newData);
                  return { ...prevState, data };
                });
              }, 600);
            }),
          onRowUpdate: (newData, oldData) =>
            new Promise(resolve => {
              setTimeout(() => {
                resolve();
                if (oldData) {
                  this.setState(prevState => {
                    const data = [...prevState.data];
                    data[data.indexOf(oldData)] = newData;
                    return { ...prevState, data };
                  });
                }
              }, 600);
            }),
          onRowDelete: oldData =>
            new Promise(resolve => {
              setTimeout(() => {
                resolve();
                this.setState(prevState => {
                  const data = [...prevState.data];
                  data.splice(data.indexOf(oldData), 1);
                  return { ...prevState, data };
                });
              }, 600);
            })
        }}
      />
    );
  }
}

export default App;
