import React from "react";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Button from "@material-ui/core/Button";
import "./App.css";
import socketIOClient from "socket.io-client";
import Axios from "axios";

class App extends React.Component {
  constructor() {
    super();
    this.socket = socketIOClient("http://localhost:8080");
    this.socket.emit("new");
    this.state = {
      items: []
    };
  }
  /*  async componentDidMount() {
    const data = await fetch("http://localhost:8080/items", {
      methog: "GET",
      headers: {
        "Content-Type": "application/json",
        Accept: "applicaiton/json"
      }
    })
      .then(function(res) {
        return res.json();
      })
      .then(function(res) {
        return res;
      })
      .catch(function(err) {
        throw err;
      });
    console.log("bonjour");
    console.log(data);
    this.setState({ items: data });
  }*/
  async getItem() {
    const data = await Axios.get("http://localhost:8080/items");
    //    console.log(data);
    const data_string = JSON.stringify(data.data);
    console.log(data_string);
    const data_array = JSON.parse(data_string);
    console.log(data_array);
    this.setState({ items: data_array });
    console.log(this.state.items.length);
  }
  render() {
    if (this.state.items.length < 1) {
      console.log(this.state.items.length);
      this.getItem();
      console.log("salut");
      return (
        <div>
          <header className="App-header">Loading</header>
        </div>
      );
    }
    console.log(this.state.items);
    return (
      <TableContainer component={Paper}>
        <Table className="" aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Items</TableCell>
              <TableCell align="right">type</TableCell>
              <TableCell align="right">price</TableCell>
              <TableCell align="right">rating</TableCell>
              <TableCell align="right">warranty years</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {this.state.items.map(item => (
              <TableRow key={item.name}>
                <TableCell component="th" scope="row">
                  {item.name}
                </TableCell>
                <TableCell align="right">{item.type}</TableCell>
                <TableCell align="right">{item.price}€</TableCell>
                <TableCell align="right">{item.rating}</TableCell>
                <TableCell align="right">{item.warranty_years}</TableCell>
                <Button variant="contained" color="primary">
                  modify
                </Button>
                <Button variant="contained" color="secondary">
                  delete
                </Button>
              </TableRow>
            ))}
            <TableRow>
              <TextField id="standard-basic" label="Standard" align="right" />
            </TableRow>
          </TableBody>
        </Table>
      </TableContainer>
    );
  }
}

export default App;
