import React from "react";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import "./App.css";
import socketIOClient from "socket.io-client";
import Axios from "axios";

class App extends React.Component {
  constructor() {
    super();
    this.socket = socketIOClient("http://localhost:8080");
    this.socket.emit("new");
    this.state = {
      items: [],
      loaded: false
    };
  }
  /*  async componentDidMount() {
    const data = await fetch("http://localhost:8080/items", {
      methog: "GET",
      headers: {
        "Content-Type": "application/json",
        Accept: "applicaiton/json"
      }
    })
      .then(function(res) {
        return res.json();
      })
      .then(function(res) {
        return res;
      })
      .catch(function(err) {
        throw err;
      });
    console.log("bonjour");
    console.log(data);
    this.setState({ items: data });
  }*/
  async getItem() {
    const data = await Axios.get("http://localhost:8080/items");
    console.log(data);
    this.setState({ items: data });
  }

  render() {
    if (this.state.loaded === false) {
      this.getItem();
      console.log("salut");
      this.setState({ loaded: true });
      return (
        <div>
          <header className="App-header">Loading</header>
        </div>
      );
    }
    console.log(this.state.items);
    return (
      <TableContainer component={Paper}>
        <Table className="" aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Items</TableCell>
              <TableCell align="right">type</TableCell>
              <TableCell align="right">price</TableCell>
              <TableCell align="right">rating</TableCell>
              <TableCell align="right">warranty years</TableCell>
              <TableCell align="right">availability</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {this.state.items.map(item => (
              <TableRow key={item.name}>
                <TableCell component="th" scope="row">
                  {item.name}
                </TableCell>
                <TableCell align="right">{item.type}</TableCell>
                <TableCell align="right">{item.price}</TableCell>
                <TableCell align="right">{item.rating}</TableCell>
                <TableCell align="right">{item.warranty_years}</TableCell>
                <TableCell align="right">{item.available}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    );
  }
}

export default App;
